+  files: File[];
+  month: string;
+  requestTime: string;
+  startTime: string;
+  status: 'Í≤∞Ïû¨ÎåÄÍ∏∞' | 'Í≤∞Ïû¨ÏôÑÎ£å' | 'Î∞òÎ†§';
